import numpy as np
from decision_tree import *
from math import *

        
class CrossValidation:
    
    def __init__(self, inputData, folds):
        self.inputData = inputData
        self.folds = folds
        self.splitData = self.getSplitData()
        self.predictedOutputs = []
        self.outputs = []

    def getSplitData(self):
        randIndices = range(self.inputData.shape[0])
        np.random.shuffle(randIndices)
        splitData = list(np.array_split(randIndices, self.folds))
        return splitData
    
    def getTrainingData(self, run):
        trainingData = []
        for i in range(self.folds):
            if i == run:
                continue
            if len(trainingData) == 0:
                trainingData = list(self.splitData[i])
            else:
                trainingData.extend(self.splitData[i])
        return trainingData
    
    def getTestingData(self, run):
        testingData = self.splitData[run]
        return testingData
    
    def addPredictedOutputs(self, predictedOutputs):
        self.predictedOutputs.append(predictedOutputs)
        
    def addOutputs(self, outputs):
        self.outputs.append(outputs)
        
    def getPrecision(self, fold):
        predictedOutputs = self.predictedOutputs[fold]
        outputs = self.outputs[fold]
        
        TP = 0
        FP = 0
        for i in range(len(outputs)):
            if predictedOutputs[i] == 1 and outputs[i] == 1:
                TP += 1
            elif predictedOutputs[i] ==1 and outputs[i] == 0:
                FP += 1
        return float(TP) / float(TP + FP)
        
    def getRecall(self, fold):
        """
        
        """
        predictedOutputs = self.predictedOutputs[fold]
        outputs = self.outputs[fold]
        
        TP = 0
        FN = 0
        for i in range(len(outputs)):
            if predictedOutputs[i] == 1 and outputs[i] == 1:
                TP += 1
            elif predictedOutputs[i] == 0 and outputs[i] == 1:
                FN += 1
        return float(TP) / float(TP + FN)

    def getAccuracy(self, fold):
        """
        
        """
        predictedOutputs = self.predictedOutputs[fold]
        outputs = self.outputs[fold]
        TP = 0
        TN = 0
        total = len(outputs)
        for i in range(len(outputs)):
            if predictedOutputs[i] == 1 and outputs[i] == 1:
                TP += 1
            elif predictedOutputs[i] == 0 and outputs[i] == 0:
                TN += 1
        return float(TP + TN) / float(total) 
    
    def getAveragePrecision(self):
        precision = 0
        for fold in range(self.folds):
            precision += self.getPrecision(fold)
        avPrecision = precision / self.folds
        return avPrecision
    
    def getAverageRecall(self):
        recall = 0
        for fold in range(self.folds):
            recall += self.getRecall(fold)
        avRecall = recall / self.folds
        return avRecall
    
    def getAverageAccuracy(self):
        accuracy = 0
        for fold in range(self.folds):
            accuracy += self.getAccuracy(fold)
        avAccuracy = accuracy / self.folds
        return avAccuracy
    
    def printConfusionMatrix(self):
        confusionMatrix = self.getConfusionMatrix()
        print("\n\t--------CONFUSION MATRIX---------")
        print("\n\t\t\t\t\t    Predicted")
        print("\t||--------------------------------------|---------------------------------------|-------------------------------||")
        print("\t||\t\tTN = %s\t\t|\t\tFP = %s\t\t| Specificity = %s\t||" % (confusionMatrix[0][0], confusionMatrix[0][1], confusionMatrix[0][2] * 100))                     
        print("\t||--------------------------------------|---------------------------------------|-------------------------------||")
        print("Actual\t||\t\tFN = " + str(confusionMatrix[1][0]) + "\t\t|\t\tTP = " + str(confusionMatrix[1][1]) + "\t\t| Recall      = " + str(confusionMatrix[1][2] * 100) + "%\t||")
        print("\t||--------------------------------------|---------------------------------------|-------------------------------||")
        print("\t||\tNPV = "+ str(confusionMatrix[2][0] * 100) + "%\t\t|\tPrecision = " + str(confusionMatrix[2][1] * 100) + "%\t| Accuracy    = " + str(confusionMatrix[2][2] * 100) + "%\t||")
        print("\t||--------------------------------------|---------------------------------------|-------------------------------||")
 
    def getConfusionMatrix(self):
        accuracy = self.getAverageAccuracy()
        precision = self.getAveragePrecision()
        recall = self.getAverageRecall()
        
        TP = 0
        TN = 0
        FP = 0
        FN = 0
        for i in range(self.folds):
            for j in range(len(self.outputs[i])):
                if self.outputs[i][j] == 1 and self.predictedOutputs[i][j] == 1:
                    TP += 1
                elif self.outputs[i][j] == 0 and self.predictedOutputs[i][j] == 0:
                    TN += 1
                elif self.outputs[i][j] == 0 and self.predictedOutputs[i][j] == 1:
                    FP += 1
                elif self.outputs[i][j] == 1 and self.predictedOutputs[i][j] == 0:
                    FN += 1
        TP /= self.folds
        TN /= self.folds
        FP /= self.folds
        FN /= self.folds
        npv = float(TN) / float(TN + FN)
        specificity = float(TN) / float(TN + FP)
        confMatrix = [[TN, FP, specificity], [FN, TP, recall], [npv, precision, accuracy]]
        return confMatrix
                    
        
